CREATE DATABASE IF NOT EXISTS geronacodes;
USE geronacodes;

CREATE TABLE tblcitizen (
  id int(11) NOT NULL AUTO_INCREMENT,
  ccode text NOT NULL,
  citiz text NOT NULL,
  PRIMARY KEY (id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

CREATE TABLE tblcountry (
  id int(11) NOT NULL AUTO_INCREMENT,
  crycode text NOT NULL,
  country text NOT NULL,
  PRIMARY KEY (id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

CREATE TABLE tblmunicipals (
  id int(11) NOT NULL AUTO_INCREMENT,
  province text NOT NULL,
  muncode text NOT NULL,
  municipals text NOT NULL,
  x text NOT NULL,
  y text NOT NULL,
  PRIMARY KEY (id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

CREATE TABLE tbloccupation (
  id int(11) NOT NULL AUTO_INCREMENT,
  ocode text NOT NULL,
  occupation text NOT NULL,
  PRIMARY KEY (id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

CREATE TABLE tblprovinces (
  id int(11) NOT NULL AUTO_INCREMENT,
  provcode text NOT NULL,
  province text NOT NULL,
  PRIMARY KEY (id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

CREATE TABLE tblreligious (
  id int(11) NOT NULL AUTO_INCREMENT,
  rcode text NOT NULL,
  relsec text NOT NULL,
  PRIMARY KEY (id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

